import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSScomponents/Homepage.css';
import NewCostumer from './NewCostumer';

export default function Homepage(props) {

    let nav = useNavigate();
    const costumersArray = props.costumers;
    const [shownData,setShownData] = useState('id');

    const onRadioChange = (e) =>{
    const {value} = e.target;
    setShownData(value);
    }

    const onSelectChange = (e) =>{
    const { value } = e.target;
    nav('/CostumerData', { state: { costumerData: value } })
    }


  return (

    <div className='homepage'>
        <h1>SV-Garage</h1>
        <div>
          <button onClick={() =>{nav('/NewCostumer',{state:{costumersArray:costumersArray}})}}>New costumer</button>
        </div>
        <div>
          <select onChange={onSelectChange}>
        {costumersArray.map((costumer) => {
        return <option key={costumer.id} value={costumer}>{costumer[shownData]}</option> 
        })}
          </select><br />
           <label htmlFor='shownData-id'>id</label>
            <input
                type='radio'
                id='shownData-id'
                name='shownData'
                onChange={onRadioChange}
                checked={shownData === 'id'}
                value='id'
            />
            <label htmlFor='shownData-platenumber'>plate number</label>
            <input
                type='radio'
                id='shownData-platenumber'
                name='shownData'
                onChange={onRadioChange}
                checked={shownData === 'plateNumber'}
                value='plateNumber'
            />
        </div>
    </div>
  )
}
