import React, { useState,useEffect,useContext } from 'react';
import { useNavigate,useParams } from 'react-router-dom';
import CustomerDetails from './CustomerDetails';
import CustomerContext from "./customer-context";
import MalfunctionList from './MalfunctionList';
import style from "../CSScomponents/CostumerData.css";


export default function CostumerData(props) {
  const navigate = useNavigate();
  const { customers } = useContext(CustomerContext);
  const { id } = useParams();
  const [details, setDetails] = useState(null);
  const [history, setHistory] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [showHistory, setShowHistory] = useState(false);

  useEffect(() => {
    // We should always have an id, but its still
    // good to check if it exists
    // /customer/:id
    // /customer/14598741
    // /customer/45789630
    if (id) {
      // use the id and search our customer array
      // for a customer with that id, if we find one
      // we return that whole object and assign it to
      // the variable "customer"
      const customer = customers.find((x) => x.id.toString() === id);
      // check if we found a customer
      if (customer) {
        // found a customer, so set the details
        // into our state
        setDetails(customer);
      }
    }
    // watch the variables 'id' and
    // 'customer' for changes, if so
    // callback function is executed
  }, [id, customers]);

  if (!details) {
    return <h3>Loading....</h3>;
  }

  function toggleShowDetails() {
    setShowDetails((prevState) => !prevState);
  }

  function toggleShowHistory() {
    setShowHistory((prevState) => !prevState);
  }

  return (
    <div>
      <div className="costumer-history-and-data">
        <button onClick={toggleShowDetails}>
          {showDetails ? "Hide" : "Show"} Customer details
        </button>
        {showDetails && <CustomerDetails customer={details} />}
        <button onClick={toggleShowHistory}>
          {showHistory ? "Hide" : "Show"} History
        </button>
        {showHistory && <div>SHOWING HISTORY</div>}
      </div>
      <div>
        <button onClick={() => navigate("/")}>Go Back</button>
      </div>
    </div>
  );
}
