import React from 'react';
import { useNavigate } from 'react-router-dom';
import style from './controlPanel.css';

export default function ControlPanel() {

  const nav = useNavigate();

  const allFlights = () => {
    nav("/ControlPanel/AllFlights");
  };

  const sortFlights = () => {
    nav("/ControlPanel/SortFlights");
  };

  const addFlights = () => {
    nav("/ControlPanel/AddFlights");
  };

  const removeFlights = () => {
    nav("/ControlPanel/RemoveFlight");
  };

  return (
    <div className='CP-buttons'>
      <div
        style={{ width: "300px", height: "250px", border: "black solid 2px" }}
      >
        <button onClick={allFlights}>
          All flights
        </button>
        <br />
        <button onClick={sortFlights}>
          Sort flights
        </button>
        <br />
        <button onClick={addFlights}>
          Add flights
        </button>
        <br />
        <button onClick={removeFlights} style={{ marginBottom: "10px" }}>
          Remove Flights
        </button>
      </div>
    </div>
  );
}
