import { createContext, useState } from "react";

const CustomerContext = createContext({
    // JUST A BLUEPRINT FOR WHAT IS IN
    // YOUR CONTEXT OBJECT (VALUE)
    customers: [],
    malfunctions: []
});

// CUSTOM REACT COMPONENT TO WRAP OUR APPLICATION
// ALLOWS ACCESS TO OUR CONTEXT OBJECT THROUGH
// useContext HOOK
export function CustomerContextProvider({ children }) {
    const [customers, setCustomers] = useState([]);
    const [malfunctions, setMalfunctions] = useState([]);
    addCustomer: (costumerData) => {}

    // ADD ALL YOU FUNCTIONS
    // FOR UPDATING/DELETING/CREATING
    // DATA IN HERE

    const addCustomer = (customerData) => {
        const id = genUID();
        customerData.id = id;
        setCustomers(prevState => [...prevState, customerData]);
    }

    return (
        // Pass any number of params into value to access them
        // on the context object
        <CustomerContext.Provider value={{ customers, malfunctions, addCustomer }}>
            {children}
        </CustomerContext.Provider>
    );
}

// BY CONVENTION DEFAULT EXPORT
// IS OUR CONTEXT OBJECT
export default CustomerContext;

function genUID() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
        const r = (Math.random() * 16) | 0,
            // eslint-disable-next-line
            v = c == 'x' ? r : (r & 0x3) | 0x8;
        return v.toString(16);
    });
}
