import React from 'react';
import './Style.css';

export default function Products(props) {

  return (
<div>
    <div className='Frame'>
  <p>{props.product} price:{props.price}₪</p>
  <button id='plus' onClick={()=>{
    {props.add(props.index)}
  }}>Add to cart</button>
    </div>
    </div>
  )
}
