import React from 'react';
import ControlPanel from './ControlPanel';

export default function AllFlights(props) {

  const flights = props.flights;
  console.log(flights);
  return (
    <div>
      <ControlPanel />
      <div
        style={{
          marginLeft: "500px",
          width: "580px",
          height: "450px",
          // border: "2px solid black",
        }}
      >
        <h1>All flights</h1>
        <div style={{display:'flex',flexDirection:'row'}}>
          {flights.map((flight,index) => {
            return <div key={index} style={{backgroundColor:'lightgray',
            width:'500px', height:'150px',marginTop:'10px',border:'2px solid black',
            marginLeft:'5px'
            }}>
              flight number: {flight.code}<br/>
              flight company : {flight.company}<br/>
              passengers: {flight.passengers}<br/>
            </div>
        }
        )}

        </div>
      </div>
    </div>
  );
}
