import React from 'react';
import './Style.css';


export default function Cart(props) {


const TotalPrice = () =>{
  let TotalPrice = 0; 

  props.CartItems.forEach((elem)=>{
    TotalPrice += elem.price;
  })
  return TotalPrice; 
}

  return (

    <div>
<h1 className='Pcolor'>Here are your selected products</h1>
<p className='Pcolor'>Total products:
    </p>
<div>
  {props.CartItems.map((val)=>{
    return <p className='Pcolor'>{val.product} {val.price}₪ </p>
  })}
  <div>
    <p className='Pcolor'>Total price:{TotalPrice()}₪</p>
    
    <button onClick={props.Buy}>Buy</button>

    <button onClick={()=>{
      props.delete(props.index)
    }}>Delete items</button>

    </div>
      </div>
     </div>
  )
}
