import './App.css';
import { useState } from 'react';
import Products from './Components/Products';
import Cart from './Components/Cart';
import './Components/Style.css';

function App() {

  const [CartItems, setCartItems] = useState([]);
  const [ProductList, setProducts] = useState([
    { product: 'Computer', price: 3500 },
    { product: 'Printer', price: 1200 },
    { product: 'Screen', price: 800 },
    { product: 'Laptop', price: 2800 },
    { product: 'Iphone 12', price: 5000 },
    { product: 'Samsung S20', price: 3800 }
  ]);

  const DeleteProduct = (index) => {
    let temp = [...CartItems];
    temp.splice(index);
    setCartItems([...temp]);
  }

  const AddItem = (index) => {
    let item = ProductList[index];
    let temp = [...CartItems, item];
    setCartItems([...temp]);
  }

  const BuyProducts = () => {
    let ShopList = [...CartItems];
    setCartItems([]);
    console.log(ShopList);
  }



  return (

    <div className="App">

      <div>
      </div>
      <div>
        {ProductList.map((val, index) => {
          return <Products add={AddItem} index={index} product={val.product} price={val.price}></Products>
        })}
        <Cart CartItems={CartItems} delete={DeleteProduct} Buy={BuyProducts}></Cart>

      </div>
    </div>
  );
}
export default App;
