import './App.css';
import Title from './Components/Title';
import Post from './Components/Post';
import { useState } from 'react';
import AddPost from './Components/AddPost';

function App() {

  let posts = [{name: 'aaa',body:'fsdfds'},{name:'bbb',body:'fsbv'},{name:'ccc',body: 'fsbfgh'}];
  const [posting,setPosting] = useState([]);
  return (

    <div className="App">
      <Title/>
      <Post posts = {posts}/>
      <AddPost posting = {posting} setPosting = {setPosting}/>
    </div>
  );
}

export default App;
