import React from 'react';
import { useState } from 'react';

export default function AddPost() {
const [bgColor,setBgColor] = useState('red');

const changeColor = () => {
    setBgColor('green');
};

  return (

    <div>
        <h1 style={{backgroundColor: bgColor}}>Add post</h1>
        <input type="text" /><br />
        <input type="text" /><br />
        <button onClick={changeColor}>Create</button>
    </div>
  )
}
