import React from 'react';
import './Post.css';

export default function Post(props) {
  const arr = props.arr;
  return (
    <div>
      {arr.map((val) => {
        return <div className='post'>
          <h1>{`The author name is: ${val.name}`}</h1>
          <p>{`The blog is: ${val.body}`}</p>
        </div>
      })}
    </div>
  );
}
