import React from 'react';
import { useState } from 'react';

export default function AddPost() {
const [bgColor,setBgColor] = useState('red');

const valid = (e) => {
    console.log(e.target.value);
}

  return (
    <div>
      <h1 style={{ backgroundColor: bgColor }}>Add post</h1>
      <input type="text" 
      onChange={valid} 
      placeholder="name" 
      />
      <br />
      <input type="text" 
      onChange={valid} 
      placeholder="price" 
      />
      <br />
      <button
        onClick={() => {
          setBgColor("green");
        }}
      >
        Create
      </button>
    </div>
  );
}
