import React from 'react';
import { useState } from 'react';

export default function AddPost({ posting,setPosting }) {
  const [name, setName] = useState("");
  const [body, setBody] = useState("");
  const whole = {};

  const test = (e) => {
    e.preventDefault();
    whole.name = name;
    whole.body = body;
    let temp = [...posting];
    temp.push(whole);
    setPosting(temp);
  };

  return (
    <form onSubmit={test}>
      <input type="text" onChange={(e) => {setName(e.target.value)}}/>
      <input type="text" onChange={(e) => {setBody(e.target.value)}}/>
      <button>click</button>
    </form>
  );
}
