import React, { useState,useContext } from "react";
import { useNavigate } from "react-router-dom";
import CustomerContext from './customer-context';
import "../CSScomponents/Homepage.css";

export default function Homepage(props) {
  
  const customersArray = props.customers;
  const navigate = useNavigate();
  const [shownData, setShownData] = useState("id");
  console.log(customersArray);

  const onRadioChange = (e) => {
    const { value } = e.target;
    setShownData(value);
  };

  const onSelectChange = (e) => {
     const { value } = e.target;
    const costumerData = customersArray.find((x) => x.id.toString() === value);
    navigate("/CostumerData", { state: { costumerData:costumerData } });
  };

  return (
    <div className="homepage">
      <div className="homepage-content">
        <h1 style={{ color: "white" }}>SV-Garage</h1>
        <button onClick={() => navigate("/NewCostumer")}>Register here</button>
        <br />
        <select onChange={onSelectChange}>
          <option value="">--list of costumers--</option>
          {customersArray.map((costumer) => (
            <option key={costumer.id} value={costumer}>
              {costumer[shownData]}
            </option>
          ))}
        </select>
        <br />
        <label htmlFor="shownData-id">id</label>
        <input
          type="radio"
          id="shownData-id"
          name="shownData"
          onChange={onRadioChange}
          checked={shownData === "id"}
          value="id"
        />
        <label htmlFor="shownData-platenumber">plate number</label>
        <input
          type="radio"
          id="shownData-platenumber"
          name="shownData"
          onChange={onRadioChange}
          checked={shownData === "plateNumber"}
          value="plateNumber"
        />
      </div>
    </div>
  );
}
