import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSScomponents/Homepage.css';
import NewCostumer from './NewCostumer';

export default function Homepage(props) {

    let nav = useNavigate();
    const costumersArray = props.costumers;
    const [shownData,setShownData] = useState('id');

    const onRadioChange = (e) =>{
    const {value} = e.target;
    setShownData(value);
    }

    const onSelectChange = (e) =>{
    const { value } = e.target;
    navigate('/CostumerData', { state: { costumerData: value } })
    }


  return (

    <div className='homepage'>
        <h1>SV-Garage</h1>
        <div>
          <button onClick={() =>{nav('/NewCostumer',{state:{costumersArray:costumersArray}})}}>New costumer</button>
        </div>
        <div>
          <select onChange={(e) => {setShownData(e.target.value)}}>
        {costumersArray.map((costumer,index) => {
        return <option onClick={() => {nav('/CostumerData',{state:{costumersArray:costumersArray,costumerIndex:index}})}} key={index}>
        {costumer.id}
        </option> 
        })}
          </select><br />
          <input type = "radio" defaultChecked name = 'shownData'/>id
          <input type = "radio" name = 'shownData' style={{marginLeft:'20px'}}/>plate number
        </div>
    </div>
  )
}
