import React from 'react'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function AddRoom(props) {

  const [name,setName] = useState('');
  const [color,setColor] = useState('');
  const [type,setType] = useState('');

  const rooms = props.rooms;
  const setRooms = props.setRooms;
  const navigate = useNavigate();

  const room = {};
  
  const onSubmitHandler = (e) => {
    e.preventDefault();
    if(name.length == 0 || name.length > 5){
      alert('room name must be between 1-5 characters');
      return;
    }
    if(type == ''){
      alert('please select the room type');
      return;
    }
    else if (name.length > 0 && name.length <= 5) {
      room.name = name;
      room.color = color;
      room.type = type;
      room.products = [];
      const tempRooms = [...rooms];
      tempRooms.push(room);
      setRooms(tempRooms);
      navigate(-1, { state: { rooms: rooms } });
    }
  }

  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <select
          className=""
          onChange={(e) => {
            setType(e.target.value);
          }}
        >
          <option value="">--please select the room type--</option>
          <option value="Bathroom">Bathroom</option>
          <option value="Bedroom">Bedroom</option>
          <option value="Kitchen">Kitchen</option>
        </select>
        <br />
        <input
          type="text"
          onChange={(e) => {
            setName(e.target.value);
          }}
          placeholder="Enter room name"
        />
        <br />
        <input
          type="text"
          onChange={(e) => {
            setColor(e.target.value);
          }}
          placeholder="Enter room color"
        />
        <br />
        <button>Create room</button>
      </form>
    </div>
  );
}
