import React, { useState } from 'react';
import {useNavigate} from 'react-router-dom';
import '../CSScomponents/Homepage.css'

export default function NewCostumer() {

    const nav = useNavigate();
    const [name,setName] = useState('');
    const [id,setId] = useState();
    const [address,setAddress] = useState('');
    const [phoneNumber,setPhoneNumber] = useState();
    const [plateNumber,setPlateNumber] = useState();

    const onSubmitHandler = (e) =>{
    e.preventDefault();
    if(typeof name !== 'string'){
    alert('name must contain letters only');
    }
    }

  return (
  
   <form onSubmit={onSubmitHandler} className='homepage'>
    <h1>Enter your details here</h1>
    <input type="text" onChange = {(e) => {setName(e.target.value)}} placeholder='Full name (letters only)'/><br />
    <input type="text" pattern = '[0-9]' onChange = {(e) => {setId(e.target.value)}} placeholder='Identification number (numbers only)'/><br />
    <input type="text" onChange = {(e) => {setAddress(e.target.value)}} placeholder='Address'/><br />
    <input type="text" pattern = '[0-9]' onChange={(e) => {setPhoneNumber(e.target.value)}} placeholder='Mobile/Phone number'/><br />
    <input type="text" pattern='[0-9]' onChange = {(e) => {setPlateNumber(e.target.value)}} placeholder = 'Plate number'/><br />
    <button>Register</button>
   </form>
  )
}
