import React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ControlPanel from './ControlPanel';

export default function AddFlights(props) {

  const [flightCode,setFlightCode] = useState('');
  const [company,setCompany] = useState('');
  const [passengers,setPassengers] = useState('');

  const flights = props.flights;
  const setFlights = props.setFlights;
  // console.log(flights);
  const nav = useNavigate();

  const newFlight = {};

  const addNewFlight = (event) => {
    event.preventDefault();
    const patt = /^[0-9]*$/g;
    const result = flightCode.match(patt);
    if(!result || flightCode.length == 0 || flightCode.length > 5){
      alert('flight number must be between 1-5 digits');
      return;
    }
    if(company.length == 0){
      alert('company name must be at least 1 character');
      return;
    }
    if(passengers < 1 || passengers > 450){
      alert('passengers seats must be between 1-450');
      return;
    }
    //looking if an object has the same code as the new object im trying to add
    const isFlightAlreadyExists = flights.find((obj) => {
      return obj.code === flightCode;
    });
    if(isFlightAlreadyExists){
      alert('flight already exists');
    }
    else{
      newFlight.code = flightCode;
      newFlight.company = company;
      newFlight.passengers = passengers;
      let temp = [...flights];
      temp.push(newFlight);
      setFlights(temp);
    }
  };
 

  return (
    <div>
      <ControlPanel />

      <form
        onSubmit={addNewFlight}
        style={{
          marginLeft: "500px",
          width: "450px",
          height: "450px",
          border: "2px solid black",
        }}
      >
        <input
          type="text"
          onChange={(e) => {
            setFlightCode(e.target.value)
          }}
          placeholder="Enter flight number"
        />
        <br />
        <input
          type="text"
          onChange={(e) => {
            setCompany(e.target.value)
          }}
          placeholder="Enter company name here"
        />
        <br />
        <input
          type="text"
          onChange={(e) => {
            setPassengers(e.target.value)
          }}
          placeholder="Enter number of passengers"
        />
        <br />

        <button>Add new flight</button>
      </form>
    </div>
  );
}
