import React, { useState } from 'react';
import {Navigate, useNavigate} from 'react-router-dom';
import '../CSScomponents/Homepage.css';
import { useLocation } from 'react-router-dom';

export default function NewCostumer(props) {

  const nav = useNavigate();
  const [name, setName] = useState("");
  const [id, setId] = useState();
  const [address, setAddress] = useState("");
  const [phoneNumber, setPhoneNumber] = useState();
  const [plateNumber, setPlateNumber] = useState();
  const costumers = props.costumers;
  const setCostumers = props.setCostumers;

  const costumer = {};

  const onSubmitHandler = (event) =>{
  event.preventDefault();
  if(!isNaN(fullName)){
  alert('name must contain letters only');
  return;
  }
  if(id.length !==9 && typeof id !== 'number'){
  alert('id must be 9 digits long');
  return;
  }
  if(phoneNumber.length < 7 && typeof phoneNumber !== 'number'){
  alert('phone must contain digits only and be at least 7 digits');
  }
  if(typeof plateNumber !== 'number'){
  alert('plate number must contain digits only');
  }
  newCostumer.name = fullName;
  newCostumer.id = id;
  newCostumer.Address = Address;
  newCostumer.phoneNumber = phoneNumber;
  newCostumer.plateNumber = plateNumber;
  let tempCostumer = [...costumers];
  tempCostumer.push(newCostumer);
  setCostumers(tempCostumer);
  Navigate('/',{state:{costumers:costumers}});
  }
   
  return (
    <div>
      <button onClick={() => navigate('/')}>Return to homepage</button>
      <form onSubmit={onSubmitHandler}>
        <input
          type="text"
          required
          onChange={(e) => {
            setFullName(e.target.value);
          }}
          placeholder="Full name"
        />
        <br />
        <input
          type="text"
          required
          onChange={(e) => {
            setId(e.target.value);
          }}
          placeholder="ID number"
        />
        <br />
        <input
          type="text"
          required
          onChange={(e) => {
            setAddress(e.target.value);
          }}
          placeholder="Address"
        />
        <br />
        <input
          type="text"
          required
          onChange={(e) => {
            setPhoneNumber(e.target.value);
          }}
          placeholder="Phone number"
        />
        <br />
        <input
          type="text"
          required
          onChange={(e) => {
            setPlateNumber(e.target.value);
          }}
          placeholder="plate number"
        />
        <br />
        <button>Register</button>
      </form>
    </div>
  );
}
