import { useState } from 'react';
import './App.css';
import AddPost from './Components/AddPost';
import Post from './Components/Post';


function App() {

  const arr = useState([{ name: 'fds', price: 1432 }, { name: 'fdsf', price: 5432}, { name: 'dasf', price: 532}]);
  return (

    <div className="App">
      {arr.map((val,index) => {
        return <div key={index}>
          <Post name = {val.name} price = {val.price}/>
        </div>
      })}
      <AddPost/>
    </div>
  );
}

export default App;
