import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../CSScomponents/Homepage.css";

export default function Homepage(props) {

  const costumersArray = props.costumers;
  const navigate = useNavigate();
  const [shownData, setShownData] = useState('id');

  const onRadioChange = (e) => {
    const { value } = e.target;
    setShownData(value);
  }

  const onSelectChange = (e) => {
    const { value } = e.target;
    if(value === ''){
    return;
    }
    navigate("/CostumerData", { state: { costumerData: value } });
  }

  return (
    <div className="homepage">
      <h1>SV-Garage</h1>
      <div>
        <button
          onClick={() =>
            navigate("/NewCostumer", { state: { costumersArray } })
          }
        >
          New Customer
        </button>
      </div>
      <select onChange={onSelectChange}>
        <option value=''>--list of costumers--</option>
        {costumersArray.map((costumer) => (
          <option key={costumer.id} value={costumer.id}>
            {costumer[shownData]}
          </option>
        ))}
      </select><br />
      <label htmlFor="shownData-id">id</label>
      <input
        type="radio"
        id="shownData-id"
        name="shownData"
        onChange={onRadioChange}
        checked={shownData === "id"}
        value="id"
      />
      <label htmlFor="shownData-platenumber">plate number</label>
      <input
        type="radio"
        id="shownData-platenumber"
        name="shownData"
        onChange={onRadioChange}
        checked={shownData === "plateNumber"}
        value="plateNumber"
      />
    </div>
  );
}
