import React, { useState } from 'react';

export default function CostumerData(props) {

  const [details, setDetails] = useState(false);
  const [history,setHistory] = useState(false);
  const costumersArray = props.costumers;

  const showDetails = () => {
  setDetails(!details);
  }

  const showHistory = () => {
  setHistory(!history);
  }

  const showCostumerData = () => {
  costumersArray.map((data) => {
  return (
    <li>
      <p>{data.name}</p>
      <p>{data.address}</p>
      <p>{data.id}</p>
      <p>{data.phoneNumber}</p>
      <p>{data.plateNumber}</p>
    </li>
  );
  })
  }

  return (
    <div>
      <div className="costumer-history-and-data">
        <button onClick={showDetails}>Costumer details</button>
        {details ? (showCostumerData) : ""}
        <button onClick={showHistory}>History</button>
        {history ? <h1>hi</h1> : ""}
      </div>
    </div>
  );
}
