import React from 'react';
import { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Malfunctions(props) {

    const [malfunctionDescripion, setMalfunctionDescripion] = useState();
    const [malfunctionCode,setMalfunctionCode] = useState();
    const [malfunctionMaintananceTime, setMalfunctionMaintananceTime] = useState();
    const [malfunctionPrice, setMalfunctionPrice] = useState();

    const costumersArray = props.costumers;
    const setCostumersArray = props.setCostumers;
    const nav = useNavigate();

    const malfunctions = {}

    const onSubmitHandler = (e) =>{
    e.preventDefault();
    if(malfunctionDescripion.name == ""){
    alert('please select a malfunction');
    return;
    }
    malfunctions.name = malfunctionDescripion;
    malfunctions.code = malfunctionCode;
    malfunctions.time = malfunctionMaintananceTime;
    malfunctions.price = malfunctionPrice;
    let tempCostumer = [...costumersArray.malfunctionsList];
    tempCostumer.push(malfunctions);
    setCostumersArray(tempCostumer);
    }

    return (
      <form onSubmit={onSubmitHandler}>
        <select onChange={(e) => {setMalfunctionDescripion(e.target.value)}}>
          <option value="">--please select the malfunction--</option>
          <option value="Paint">Paint</option>
          <option value="tinsmithing">tinsmithing</option>
          <option value="yearlyMaintanance">yearly maintanance</option>
          <option value="skeletonDamage">skeleton damage</option>
        </select>

        <input type="text" onChange={(e) => {setMalfunctionCode(e.target.value)}} placeholder="malfunction code" />
        <input type="text" onChange={(e) => {setMalfunctionMaintananceTime(e.target.value)}} placeholder="malfuntction maintanance time" />
        <input type="text" onChange={(e) => {setMalfunctionPrice(e.target.value)}} placeholder="malfunction price" />
        <button>click me</button>
      </form>
    );
}
