import React, { useState } from 'react'

export default function NewCostumer() {

    const [name,setName] = useState('');
    const [id,setId] = useState();
    const [address,setAddress] = useState('');
    const [phoneNumber,setPhoneNumber] = useState();
    const [plateNumber,setPlateNumber] = useState();

    const onSubmitHandler = (e) =>{
    e.preventDefault();
    
    }

  return (

   <form>
    <h1>Enter your details here</h1>
    <input type="text" pattern = '[a-zA-Z]' onChange = {(e) => {setName(e.target.value)}} placeholder='Full name (letters only)'/><br />
    <input type="text" pattern = '[0-9]' onChange = {(e) => {setId(e.target.value)}} placeholder='Identification number (numbers only)'/><br />
    <input type="text" minLength={1} onChange = {(e) => {setAddress(e.target.value)}} placeholder='Address'/><br />
    <input type="text" pattern='[0-9]' minLength = {7} maxLength onChange={(e) => {setPhoneNumber(e.target.value)}} placeholder='Mobile/Phone number'/><br />
    <input type="text" onChange={(e) => {setPlateNumber(e.target.value)}} placeholder='Plate number'/><br />
    <button>Register</button>
   </form>
  )
}
