import React, { useState } from 'react';
import CostumerDetails from './CostumerDetails';
import style from "../CSScomponents/CostumerData.css";
import { useLocation, useNavigate } from 'react-router-dom';

export default function CostumerData(props) {

  const [details, setDetails] = useState(false);
  const [history,setHistory] = useState(false);
  const costumersArray = props.costumers;
  const navigate = useNavigate();
  
  const location = useLocation();
  const { costumerData } = location.state;
  console.log(costumerData);
  
  const showDetails = () => {
  setDetails(!details);
  }

  const showHistory = () => {
  setHistory(!history);
  }

  return (
    <div>
      <button
        onClick={() => {
          navigate("/");
        }}
      >
        return to homepage
      </button>
      <div className="costumer-history-and-data">
        <div className="button-1">
          <button className="data-buttons" onClick={showDetails}>
            Costumer details
          </button>
          {details ? <CostumerDetails costumersArray={costumersArray} /> : ""}
        </div>
        <div className="button-2">
          <button className="data-buttons" onClick={showHistory}>
            History
          </button>
        </div>
      </div>
    </div>
  );
}
