import React, { useState,useContext,useEffect } from 'react';
import { useNavigate} from 'react-router-dom';
import CustomerContext from "./customer-context";
import "../CSScomponents/NewCostumer.css";

export default function NewCostumer() {
  const nav = useNavigate();
  const { customers, addCustomer } = useContext(CustomerContext);
  const [name, setName] = useState("");
  const [id, setId] = useState();
  const [address, setAddress] = useState("");
  const [phoneNumber, setPhoneNumber] = useState();
  const [plateNumber, setPlateNumber] = useState();
  const [loading, setLoading] = useState(false);

useEffect(() => {
  if (loading && customers.find((x) => x.id === id)) {
    nav("/");
  }
}, [nav, id, customers, loading]);

  const onSubmitHandler = (e) => {
    e.preventDefault();
    if (!isNaN(name)) {
      alert("name must contain letters only");
      return;
    }
    if (typeof id !== "number" && id.length !== 9) {
      alert("id must contain 9 digits");
      return;
    }
    if (customers.find((x) => x.id === id)) {
      alert("id already in use");
      return;
    }
    if (phoneNumber.length < 7 && phoneNumber.length > 9) {
      alert("this area must contain between 7-9 digits");
      return;
    }
    if (plateNumber.length < 7 && plateNumber.length > 9) {
      alert("not a valid number");
      return;
    }
    setLoading(true);
    const customer = { id, name, address, phoneNumber, plateNumber };
    addCustomer(customer);
  };

  return (
    <div className="new-costumer-form">
      <button onClick={() => {nav('/')}}>return to homepage</button>
      <form onSubmit={onSubmitHandler} className="new-costumer-form">
        <h1>Enter your details here</h1>
        <input
          className="new-costumer-inputs"
          type="text"
          onChange={(e) => {
            setName(e.target.value);
          }}
          placeholder="Full name (letters only)"
        />
        <br />
        <input
          className="new-costumer-inputs"
          type="text"
          onChange={(e) => {
            setId(e.target.value);
          }}
          placeholder="Identification number (numbers only)"
        />
        <br />
        <input
          className="new-costumer-inputs"
          type="text"
          onChange={(e) => {
            setAddress(e.target.value);
          }}
          placeholder="Address"
        />
        <br />
        <input
          className="new-costumer-inputs"
          type="text"
          onChange={(e) => {
            setPhoneNumber(e.target.value);
          }}
          placeholder="Mobile/Phone number"
        />
        <br />
        <input
          className="new-costumer-inputs"
          type="text"
          onChange={(e) => {
            setPlateNumber(e.target.value);
          }}
          placeholder="Plate number"
        />
        <br />
        <button>Register</button>
      </form>
    </div>
  );
}
