import React from 'react'
import { useLocation, useNavigate } from 'react-router-dom';

export default function ControlPanel() {

  const nav = useNavigate();
  const location = useLocation;
  // const {flights} = location.state;
  // console.log(flights);

  const allFlights = () => {
    nav("/ControlPanel/AllFlights");
  };

  const sortFlights = () => {
    nav("/ControlPanel/SortFlights");
  };

  const addFlights = () => {
    nav("/ControlPanel/AddFlights");
  };

  const removeFlights = () => {
    nav("/ControlPanel/RemoveFlight");
  };

  return (
    <div>
      <div
        style={{ width: "300px", height: "250px", border: "black solid 2px" }}
      >
        <button onClick={allFlights}>All flights</button>
        <br />
        <button onClick={sortFlights}>Sort flights</button>
        <br />
        <button onClick={addFlights}>Add flights</button>
        <br />
        <button onClick={removeFlights}>Remove Flights</button>
      </div>
    </div>
  );
}
