import { useState } from 'react';
import './App.css';
import AddPost from './Components/AddPost';
import Post from './Components/Post';


function App() {

  const [posts,setPosts] = useState([{ name: 'fds', price: 20 }, { name: 'fdsf', price: 100}, { name: 'dasf', price: 30}]);

  const addPost = (n,p) => {
    let tempObj = {
      name: n,
      price:p
    }
    setPosts([...posts,tempObj]);
  };

  const deletePost = (index) => {
    let tempPosts = [...posts];
    tempPosts.splice(index,1);
    setPosts([...tempPosts]);
  }

  return (

    <div className="App">
      {posts.map((val,index) => {
        return <div key={index}>
          <Post index = {index} name = {val.name} price = {val.price}/>
        </div>
      })}
      <AddPost add = {addPost}/>
    </div>
  );
}

export default App;
