import React, { useState,useEffect,useContext } from 'react';
import { useNavigate,useParams } from 'react-router-dom';
import CustomerDetails from './CustomerDetails';
import CustomerContext from "./customer-context";
import style from "../CSScomponents/CostumerData.css";


export default function CostumerData(props) {
  const { customers } = useContext(CustomerContext);
  const { id } = useParams();
  const [details, setDetails] = useState(null);
  const [history, setHistory] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      const customer = customers.find((x) => x.id.toString() === id);
      if (customer) {
        setDetails(customer);
      }
    }
  }, [id, customers]);

  if (!details) {
    return <h3>Loading....</h3>;
  }

  function toggleShowDetails() {
    setShowDetails((prevState) => !prevState);
  }

  return (
    <div>
      <div className="costumer-history-and-data">
        <button onClick={toggleShowDetails}>
          {showDetails ? "Hide" : "Show"} Customer details
        </button>
        {showDetails && <CustomerDetails customer={details} />}
        <button>Show History</button>
      </div>
    </div>
  );
}
