import React, { useState } from 'react';
import {useNavigate} from 'react-router-dom';
import '../CSScomponents/Homepage.css';
import { useLocation } from 'react-router-dom';

export default function NewCostumer(props) {

    const nav = useNavigate();
    const [name,setName] = useState('');
    const [id,setId] = useState();
    const [address,setAddress] = useState('');
    const [phoneNumber,setPhoneNumber] = useState();
    const [plateNumber,setPlateNumber] = useState();
    const costumers = props.costumers;
    const setCostumers = props.setCostumers;

    const location = useLocation();
    const {costumerData} = location.state;
    console.log(costumerData);

    const costumer = {};

    const onSubmitHandler = (e) =>{
    e.preventDefault();
    if(!isNaN(name)){
    alert('name must contain letters only');
    return;
    }
    if(typeof id !== 'number' && id.length !=9){
    alert('id must contain 9 digits');
    return;
    }
    if(phoneNumber.length < 7 && phoneNumber.length >9){
    alert('this area must contain between 7-9 digits');
    return;
    }
    if(plateNumber.length < 7 && plateNumber.length > 9){
    alert('not a valid number');
    return;
    }
    else{
    costumer.name = name;
    costumer.id = id;
    costumer.address = address;
    costumer.phoneNumber = phoneNumber;
    costumer.plateNumber = plateNumber;
    // costumer.malfunctionsList = [];
    let tempCostumer = [...costumers];
    tempCostumer.push(costumer);
    setCostumers(tempCostumer);
    nav(-1,{state:{costumers:costumers}});
    }
    }

  return (
    <form onSubmit={onSubmitHandler} className="homepage">
      <h1>Enter your details here</h1>
      <input
        type="text"
        onChange={(e) => {
          setName(e.target.value);
        }}
        placeholder="Full name (letters only)"
      />
      <br />
      <input
        type="text"
        onChange={(e) => {
          setId(e.target.value);
        }}
        placeholder="Identification number (numbers only)"
      />
      <br />
      <input
        type="text"
        onChange={(e) => {
          setAddress(e.target.value);
        }}
        placeholder="Address"
      />
      <br />
      <input
        type="text"
        onChange={(e) => {
          setPhoneNumber(e.target.value);
        }}
        placeholder="Mobile/Phone number"
      />
      <br />
      <input
        type="text"
        onChange={(e) => {
          setPlateNumber(e.target.value);
        }}
        placeholder="Plate number"
      />
      <br />
      <button>Register</button>
    </form>
  );
}
