import React from 'react';
import { useState } from 'react';
import ControlPanel from "./ControlPanel";
import './Sort.css';

export default function SortFlight(props,{options}) {
  
  const flights = props.flights;
  const setFlights = props.setFlights;
  
  options = flights.map((flight) => {
    return flight.company;
  });

  const onSelectChange = (e) => {
    const sortDirection = e.target.value;
    const copyArray = [...flights];
    copyArray.sort((a,b) => {
      return sortDirection === "0" ? a.passengers - b.passengers : b.passengers - a.passengers;
    });
      setFlights(copyArray);
  };


  const [value, setValue] = useState("");
  const [showSuggestion, setShowSuggestions] = useState(false);
  
  const suggestions = options.filter((option) =>
    option.toLowerCase().includes(value.toLowerCase())
  );


  const handleChange = (e) => {
    setValue(e.target.value);
  };

  return (
    <div>
      <ControlPanel />
      <div
        style={{
          marginLeft: "500px",
        }}
      >
        <h1>Sort flights</h1>
        <input
          type="text"
          value={value}
          onChange={handleChange}
          onFocus={() => {
            setShowSuggestions(true);
          }}
          placeholder="Search"
          style={{
            margin: "10px 40px",
            fontSize: "20px",
            padding: "10px 10px 0 10px",
            outline: "none",
          }}
        />
        {suggestions && (
          <ul>
            {suggestions.map((suggestion, index) => {
              return <li key={index}>{suggestion}</li>;
            })}
          </ul>
        )}
      </div>
      <div style={{ marginLeft: "550px" }}>
        <select defaultValue={0} onChange={onSelectChange}>
          <option value={0}>Ascending</option>
          <option value={1}>Decending</option>
        </select>
        <div style={{display:'flex',flexDirection:'row'}}>
          {flights.map((flight,index) => {
            return <div key={index} style={{backgroundColor:'lightgray',
            width:'170px', height:'120px',marginTop:'10px',border:'2px solid black',
            marginLeft:'5px'
            }}>
              flight number: {flight.code}<br/>
              flight company : {flight.company}<br/>
              passengers: {flight.passengers}<br/>
            </div>
        }
        )}

        </div>
      </div>
    </div>
  );
}
