import React, { useState } from 'react';
import CostumerDetails from './CostumerDetails';
import style from "../CSScomponents/CostumerData.css";
import { useLocation, useNavigate } from 'react-router-dom';
import MalfunctionList from './MalfunctionList';

export default function CostumerData(props) {

  const [details, setDetails] = useState(false);
  const [history,setHistory] = useState(false);
  const [malfunctionCode,setMalfunctionCode] = useState([]);
  const costumersArray = props.costumers;
  const navigate = useNavigate();
  
  const location = useLocation();
  const { costumerData } = location.state;
  console.log(costumerData);
  
  const showDetails = () => {
  setDetails(!details);
  }

  const showHistory = () => {
  setHistory(!history);
  }

  return (
    <div>
      <button
        onClick={() => {
          navigate("/");
        }}
      >
        return to homepage
      </button>
      <div className="main-costumer-data">
        <button className="data-buttons-details" onClick={showDetails}>
          Costumer details
          {details ? <CostumerDetails costumersArray={costumersArray} /> : ""}
        </button>
        <button className="data-buttons-history" onClick={showHistory}>
          History
          {history ? <MalfunctionList/> : ''}
        </button>
      </div>
    </div>
  );
}
