import React, { useState } from 'react';
import style from "../CSScomponents/CostumerData.css";
import { useLocation, useNavigate } from 'react-router-dom';
import CostumerDetails from './CostumerDetails';

export default function CostumerData(props) {
    
    const costumersArray = props.costumers;
    const [details,setDetails] = useState();
    const [history,setHistory] = useState();

    const showDetails = () =>{
    setDetails(!details);
    }

    const showHistory = () =>{
    setHistory(!history);
    }

 return(
    
  <div>
    <button onClick={showDetails}>Costumer details</button>
    {details ? (<CostumerDetails costumersArray = {costumersArray}/>) : '' }
    <button onClick={showHistory}></button>
  </div>
 )
}
