import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../CSScomponents/Homepage.css";

export default function Homepage({costumers}) {
  
  const [shownData,setShownData] = useState();
  const navigate = useNavigate();

  const onRadioChange = (e) => {
    const { value } = e.target;
    setShownData(value);
  };

  const onSelectChange = (e) => {
    const { value } = e.target;
    navigate("/CostumerData", { state: { costumerData: value } });
    console.log("value");
  };

  return (
    <div className="homepage">
      <div className="homepage-content">
        <h1 style={{ color: "white" }}>SV-Garage</h1>
        <button onClick={() => navigate("/NewCostumer")}>Register here</button>
        <br />
        <option value=''>--list of costumers--</option>
        {costumersArray.map((costumer) => (
          <option key={costumer.id} value={costumer}>
            {costumer[shownData]}
          </option>
        ))}
        <label htmlFor="shownData-id">id</label>
        <input
          type="radio"
          id="shownData-id"
          name="shownData"
          onChange={onRadioChange}
          checked={shownData === "id"}
          value="id"
        />
        <label htmlFor="shownData-platenumber">plate number</label>
        <input
          type="radio"
          id="shownData-platenumber"
          name="shownData"
          onChange={onRadioChange}
          checked={shownData === "plateNumber"}
          value="plateNumber"
        />
      </div>
    </div>
  );
}
