import React from 'react';
import { useState } from 'react';

export default function AddPost() {
const [name,setName] = useState('');
const [price,setPrice] = useState('');

const validName = () => {
    if(name.length > 5){
        return <p style={{color:'crimson',background:'#f3f3f3',width:'100%'}}>Can't be more than 5 characters</p>
    }
};

  return (
    <div>
      <h1>Add post</h1>
      <input type="text" 
        onChange={(e) => {setName(e.target.value)}} 
        placeholder="name" 
      />
      <br />
      {validName()}
      <input type="text" 
        onChange={(e) => {setPrice(e.target.value)}} 
        placeholder="price" 
      />
      <br />
      <button

      >
        Create
      </button>
    </div>
  );
}
