import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSScomponents/Homepage.css';
import NewCostumer from './NewCostumer';

export default function Homepage(props) {

    let nav = useNavigate();
    const costumersArray = props.costumers;

  return (

    <div className='homepage'>
        <h1>SV-Garage</h1>
        <div>
          <button onClick={() =>{nav('/NewCostumer',{state:{costumersArray:costumersArray}})}}>New costumer</button>
        </div>
        <div>
          <select>
        {costumersArray.map((costumer,index) => {
        return <option onClick={nav('/CostumerData',{state:{costumersArray:costumersArray,costumerIndex:index}})} key={index}>
        {costumer.id}
        </option> 
        })}
        {/* return <div onClick={() => {nav('/Room',{state:{rooms:rooms,roomIndex:index}})}} className='rooms'
    key={index} style={{backgroundColor:room.color,color:`${room.color=='black'?'white':'black'}`}}>
    {room.name}
    </div> */}
          </select>
        </div>
    </div>
  )
}
